import React, { Component } from "react";
import { StyleSheet } from "react-native";
import SectionedMultiSelect from "react-native-sectioned-multi-select";
import {
  Header,
  Title,
  Container,
  Content,
  Body,
  Button,
  Text,
  Icon,
  Right,
  Left,
  Form,
  ListItem,
  Item,
  Label,
  Input,
  DatePicker,
} from "native-base";
import SecurityService from "../security/SecurityService";

// Redux
import PropTypes from "prop-types";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";

// Custom Actions


// START IMPORT ACTIONS
import TeacherActions from "../redux/actions/TeacherActions";
import CourseActions from "../redux/actions/CourseActions";
import StudentActions from "../redux/actions/StudentActions";

// END IMPORT ACTIONS

/** APIs

* actionsTeacher.create
*	@description CRUD ACTION create
*
* actionsTeacher.update
*	@description CRUD ACTION update
*	@param ObjectId id - Id
*
* actionsTeacher.get
*	@description CRUD ACTION get
*	@param ObjectId id - Id resource
*
* actionsCourse.list
*	@description CRUD ACTION list
*
* actionsStudent.list
*	@description CRUD ACTION list
*

**/


class TeacherEdit extends Component {
  
  // Init teacher
  constructor(props) {
    super(props);
    this.state = {
      teacher: {},
      authorized: false
    };
  }

  // Load data on start
  componentWillMount() {

    this.props.navigation.addListener("willFocus", async () => { 
      // Check security
      if (await SecurityService.isAuth([  ])) {
        this.setState({ authorized: true });
      } else {
        this.props.navigation.navigate("Login", {
          showError: "Not authorized"
        });
        return;
      }


      // Load data
      const itemId = this.props.navigation.getParam("id", "new");
      if (itemId !== "new") {
        this.props.actionsTeacher.loadTeacher(itemId);
      } else {
        this.setState({
          teacher: {}
        });
      }
      
      this.props.actionsCourse.loadCourseList();
      this.props.actionsStudent.loadStudentList();
    });
  }

  // Clear reducer
  componentWillUnmount() {
    this.setState({
      teacher: {}
    });
    this.props.actionsTeacher.reset();
  }

  // Insert props teacher in state
  componentWillReceiveProps(props) {
    this.setState({
      teacher: props.teacher
    });
  }

  // Save data
  save() {
    // Validation
    let errors = {};
    
    if (!this.state.teacher.firstName || this.state.teacher.firstName.trim() === "") {
      errors.firstName = true;
    }
    

    this.setState({ errors: errors });
    if (Object.keys(errors).length > 0) {
      return;
    }

    // Save
    if (this.state.teacher._id) {
      // Edit
      this.props.actionsTeacher.saveTeacher(this.state.teacher).then(data => {
        this.props.navigation.navigate("TeacherList");
      });
    } else {
      // Create
      this.props.actionsTeacher.createTeacher(this.state.teacher).then(data => {
        this.props.navigation.navigate("TeacherList");
      });
    }
  }

  // Show content
  render() { 

    // Check security
    if (!this.state.authorized) {
      return null;
    }

    return (
      <Container>
        <Header>
          <Left>
            <Button
            transparent
            onPress={() => this.props.navigation.goBack()}
            >
              <Icon name="arrow-back" />
            </Button>
          </Left>
          <Body>
            <Title>Detail Teacher</Title>
          </Body>
          <Right>
            <Button transparent onPress={() => this.save()}>
              <Text>Save</Text>
            </Button>
          </Right>
        </Header>
        <Content>
          <Form>
            
            <Item floatingLabel>
              <Label>
                Address
              </Label>
              <Input
                onChangeText={value =>
                  this.setState(Object.assign(this.state.teacher, { address: value }))
                }
                value={this.state.teacher.address && this.state.teacher.address.toString()}
              />
            </Item>
          
            
            <Item floatingLabel>
              <Label>
                BloodGroup
              </Label>
              <Input
                onChangeText={value =>
                  this.setState(Object.assign(this.state.teacher, { bloodGroup: value }))
                }
                value={this.state.teacher.bloodGroup && this.state.teacher.bloodGroup.toString()}
              />
            </Item>
          
            <Item stackedLabel>
              <Label>
                Dob
              </Label>
              <DatePicker
                placeHolderText="Select a date"
                defaultDate={this.state.teacher.dob }
                onDateChange={value => 
                  this.setState(Object.assign(this.state.teacher, { dob: value }))
                }
              />
            </Item>
            
            
            <Item floatingLabel {...(this.state.errors && this.state.errors.firstName === true ? { style: styles.validatorItem } : {})}>
              <Label
                {...(this.state.errors && this.state.errors.firstName === true ? { style: styles.validatorLabel } : {})}>
                FirstName *
              </Label>
              <Input
                onChangeText={value =>
                  this.setState(Object.assign(this.state.teacher, { firstName: value }))
                }
                value={this.state.teacher.firstName && this.state.teacher.firstName.toString()}
              />
            </Item>
            {this.state.errors && this.state.errors.firstName === true && (
              <Text style={styles.validatorMessage}>Value is required</Text>
            )}
          
            
            <Item floatingLabel>
              <Label>
                LastName
              </Label>
              <Input
                onChangeText={value =>
                  this.setState(Object.assign(this.state.teacher, { lastName: value }))
                }
                value={this.state.teacher.lastName && this.state.teacher.lastName.toString()}
              />
            </Item>
          

          {/* RELATIONS */}
          
          {/* Relation m:m _course with Course */}
          
          <Item stackedLabel>              
            <Label >
              _course
            </Label>
            <SectionedMultiSelect
              items={this.props.listCourse }
              displayKey="_id"
              uniqueKey="_id"
              selectText="Choose some items..."
              alwaysShowSelectText={true}
              modalAnimationType={"slide"}
              renderSelectText={() => {
                return "Choose some items...";
              }}
              onSelectedItemsChange={value =>
                this.setState(Object.assign(this.state.teacher, { _course: value }))
              }
              selectedItems={this.state.teacher._course }
            />
          </Item>
          
          
          {/* Relation m:m _student with Student */}
          
          <Item stackedLabel>              
            <Label >
              _student
            </Label>
            <SectionedMultiSelect
              items={this.props.listStudent }
              displayKey="_id"
              uniqueKey="_id"
              selectText="Choose some items..."
              alwaysShowSelectText={true}
              modalAnimationType={"slide"}
              renderSelectText={() => {
                return "Choose some items...";
              }}
              onSelectedItemsChange={value =>
                this.setState(Object.assign(this.state.teacher, { _student: value }))
              }
              selectedItems={this.state.teacher._student }
            />
          </Item>
          
          

          </Form>
        </Content>
      </Container>
    );
  }
}

// Store actions
const mapDispatchToProps = function(dispatch) {
  return { 
    actionsTeacher: bindActionCreators(TeacherActions, dispatch),
    actionsCourse: bindActionCreators(CourseActions, dispatch),
    actionsStudent: bindActionCreators(StudentActions, dispatch),
  };
};

// Validate types
TeacherEdit.propTypes = { 
  actionsTeacher: PropTypes.object.isRequired,
  actionsCourse: PropTypes.object.isRequired,
  actionsStudent: PropTypes.object.isRequired,
};

// Get props from state
function mapStateToProps(state, ownProps) {
  return {
    teacher: state.TeacherEditReducer.teacher,
    listCourse: state.TeacherEditReducer.listCourse,
    listStudent: state.TeacherEditReducer.listStudent
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(TeacherEdit);

const styles = StyleSheet.create({
  validatorItem: { borderColor: "red" },
  validatorLabel: { color: "red" },
  validatorMessage: { color: "red", marginLeft: 15, marginTop: 5 }
});
